import React, { useState, useEffect } from 'react';

function HomePage() {
    const [evenements, setEvenements] = useState([]);

    useEffect(() => {
        fetch('http://localhost:3001/routes/evenements')
        .then(response => response.json())
        .then(evenements => {
            console.log('Événements récupérés:', evenements);
            // Vous pouvez maintenant utiliser les données des événements comme nécessaire
        })
        .catch(error => {
            console.error('Erreur lors de la récupération des événements:', error);
        });
    }, []);
    

    return (
        <div>
            <nav className="navbar navbar-expand-lg navbar-light bg-light">
                <a className="navbar-brand" href="./Homepage">Accueil</a>
                <button className="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarNav" aria-controls="navbarNav" aria-expanded="false" aria-label="Toggle navigation">
                    <span className="navbar-toggler-icon"></span>
                </button>
                <div className="collapse navbar-collapse" id="navbarNav">
                    <ul className="navbar-nav">
                        <li className="nav-item">
                            <a className="nav-link active" href="CreationEvennement">Creer un Evennements</a>
                        </li>
                        <li className="nav-item">
                            <a className="nav-link" href="#">Vignerons</a>
                        </li>
                        <li className="nav-item">
                            <a className="nav-link" href="#">Erreur</a>
                        </li>
                    </ul>
                </div>
            </nav>
            <div className="container mt-4">
                {evenements.map(event => (
                    <div key={event.id} className="card horizontal">
                        <div className="card-stacked">
                            <div className="card-content">
                                <p>{event.name}</p>
                                <p>{event.lieu}</p>
                                <p>Nombre de personnes : {event.nombre_de_personnes}</p>
                                <p>Objectifs : {event.Objectif_de_l_evenement.join(', ')}</p>
                            </div>
                        </div>
                    </div>
                ))}
            </div>
        </div>
    );
}

export default HomePage;
