            }));
        } else {
            setFormData(prev => ({
                ...prev,
                [name]: value
            }));
        }
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        const finalFormData = {
            ...formData,
            vignerons: eventVignerons
        };
        fetch('http://localhost:3001/api/evenements', {
            method: 'POST',
            headers: {
