import React, { useState, useEffect } from 'react';
import FullCalendar from '@fullcalendar/react';
import dayGridPlugin from '@fullcalendar/daygrid';
import { Link } from 'react-router-dom';

function HomePage() {
    const [events, setEvenements] = useState([]);

    useEffect(() => {
        fetch('http://localhost:3001/api/evenements')
        .then(response => response.json())
        .then(data => {
            console.log('Data received:', data); 
            setEvenements(data);
        })
        .catch(error => {
            console.error('Fetch error:', error);
        });
    }, []);
    

    return (
        <div>
            <nav className="navbar navbar-expand-lg navbar-light bg-light">
                <a className="navbar-brand" href="/">Accueil</a>
                <button className="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarNav" aria-controls="navbarNav" aria-expanded="false" aria-label="Toggle navigation">
                    <span className="navbar-toggler-icon"></span>
                </button>
                <div className="collapse navbar-collapse" id="navbarNav">
                    <ul className="navbar-nav">
                        <li className="nav-item">
                            <a className="nav-link" href="/creationEvennement">Créer un Événement</a>
                        </li>
                        <li className="nav-item">
                            <a className="nav-link" href="/login">Identification</a>
                        </li>
                        <li className="nav-item">
                            <a className="nav-link" href="/PageVignerons">Vignerons</a>
                        </li>
                    </ul>
                </div>
            </nav>
            <FullCalendar
                plugins={[dayGridPlugin]}
                initialView="dayGridMonth"
                events={events}
                locale="fr"
                eventContent={renderEventContent} 
            />
        <h2>Liste des Événements</h2>
            {events.length > 0 ? (
                events.map(evt => (
                    <div key={evt.id}>
                        <h2>{evt.name}</h2>
                        <p>Lieu: {evt.lieu}</p>
                        <p>Date: {new Date(evt.created).toLocaleDateString()}</p>
                        <a className="nav-link" href={`/Evennement?id=${evt.id}`}>Voir Evennement</a>
                    </div>
                ))
            ) : (
                <p>Aucun événement à afficher</p>
            )}
            
            
        </div>
    );

    function renderEventContent(eventInfo) {
        return (
            <div>
                <Link to={`/event/${eventInfo.event.id}`}>{eventInfo.event.title}</Link>
                {/* Vous pouvez ajouter plus de détails ici si nécessaire */}
            </div>
        );
    }
    
}

export default HomePage;
