import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';

function EventPage() {
    const { id } = useParams(); // Récupère l'ID de l'événement à partir de l'URL
    const [event, setEvent] = useState(null);

    useEffect(() => {
        // Fetch l'événement depuis le backend en utilisant l'ID
        fetch(`http://localhost:3001/api/evenements/${id}`)
        .then(response => response.json())
        .then(data => setEvent(data))
        .catch(error => console.error('Error fetching event details:', error));
    }, [id]);

    if (!event) {
        return <div>Loading...</div>;
    }

    return (
        <div>
            <h1>{event.name}</h1>
            <p>Date: {new Date(event.created).toLocaleDateString()}</p>
            <p>Lieu: {event.lieu}</p>
            <p>Description: {event.description}</p>
            {/* Vous pouvez ajouter plus de détails ici */}
        </div>
    );
}

export default EventPage;
