import React, { useState } from 'react';
import './bootstrap.css';
import formatType from '../helpers/format-type';
import { Navigate } from 'react-router-dom';

const HomePage = ({ event, borderColor = '#009688' }) => {

    const [color, setColor] = useState(borderColor);

    const showBorder = () => {
        setColor(borderColor);
    }

    const hideBorder = () => {
        setColor('#f5f5f5');
    }

    const goToEvent = (id) => {
        return <Navigate to={`/events/${id}`} />;
    }

    const formatDate = (date) => {
        return `${date.getDate()}/${date.getMonth() + 1}/${date.getFullYear()}`;
    }

    return (
        <div className="col s6 m4" onClick={() => goToEvent(event.id)} onMouseEnter={showBorder} onMouseLeave={hideBorder}>
            <div className="card horizontal" style={{ borderColor: color }}>
                <div className="card-image">
                    <img src={event.pictureUrl} alt={event.name} />
                </div>
                <div className="card-stacked">
                    <div className="card-content">
                        <p>{event.name}</p>
                        <p><small>{formatDate(event.date)}</small></p>
                        {event.types.map(type => (
                            <span key={type} className={formatType(type)}>{type}</span>
                        ))}
                    </div>
                </div>
            </div>
        </div>
    );
}

export default HomePage;
