                            <option value="Exterieur">Extérieur</option>
                            <option value="Interieur et Exterieur">Intérieur et Extérieur</option>
                        </select>
                    </div>
                    
                    {formData.typeLieu === 'Interieur' && (
                        <div className="mb-3">
